import { useState, useRef } from 'react'
import { useStreamCustomersByFnSubscription } from '../generated/graphql';
import StreamTable from './StreamTable';


export const Search = () => {
  const [searchTerm, setSearchTerm] = useState<string>('');
  const { data, loading, error } = useStreamCustomersByFnSubscription({
    variables: { fn_ilike: searchTerm },
  })
  const poolData = useRef<any>([]);
  const handleChange = (e: any) => {
    setSearchTerm(`${e.target.value}%`);
  }



  if (data) {
    if (loading)
    console.log(poolData, data)
    poolData.current = ([...poolData.current, ...(data ? data?.customers_stream : [])])
  }

  return (
    <>
      <input
        onChange={handleChange}
      />
      {data &&
        <StreamTable data={poolData.current} />

      }
    </>
  )
}
