import axios from 'axios';
import { lazy, Suspense, useContext, useState } from 'react'
import '../App.css';
import { DataProvider } from '../Context/DataContext';
import { CallbackButton } from './CallbackButton';
const LazyTable = lazy(() => import('./Table'))

export type ContextType = {
  data: {
    data: {
      customers: { email_address: string, first_name: string, last_name: string }[]
    }
  }
}

export const Display = ({ url }: { url: string }) => {
  const { stale, setStale } = useContext()
  const onAdd = () => {
    const url = "http://localhost:8080/v1/graphql"
    const options = {
      method: "post",
      headers: {
        'Content-type': 'application/json'
      },
      data: JSON.stringify({
        query: `
          mutation addCustomer {
            insert_customers(
              objects: {
                customer_id: "this-is-a-customerId",
                email_address: "test@test.com",
                last_name: "James",
                first_name: "LeBron"
              }
            ) {
              affected_rows
            }
          }
        `,
        operationName: "addCustomer"
      })
    }
    setStale(true)
    axios(url, options)
  }

  const onRemove = () => {
    const url = "http://localhost:8080/v1/graphql"
    const options = {
      method: "post",
      headers: {
        'Content-type': 'application/json'
      },
      data: JSON.stringify({
        query: `
          mutation deleteCustomer {
            delete_customers_by_pk(customer_id: "this-is-a-customerId") {
              email_address
              first_name
              last_name
            }
          }
        `,
        operationName: "deleteCustomer",
      })
    }
    setStale(true)
    axios(url, options)
  }

  return (
    <>
      <CallbackButton callback={onAdd} text="Add"></CallbackButton>
      <CallbackButton callback={onRemove} text="remove"></CallbackButton>
      <DataProvider<ContextType> requestMetas={{
        url: "http://localhost:8080/v1/graphql", options: {
          method: "post",
          data: JSON.stringify({
            query: `
              query GetCustomers {
                customers(limit: 10, where: {first_name: {_like: "Le%"}}) {
                  email_address
                  first_name
                  last_name
                }
              }
            `,
            operationName: "GetCustomers"
          })
        }
      }}>
        <Suspense fallback={<>Loading...</>} >
          <LazyTable />
        </Suspense>
      </DataProvider>
      <DataProvider<ContextType> requestMetas={{
        url: "http://localhost:8080/v1/graphql", options: {
          method: "post",
          data: JSON.stringify({
            query: `
              query GetCustomers {
                customers(limit: 19, where: {first_name: {_like: "Le%"}}) {
                  email_address
                  first_name
                  last_name
                }
              }
            `,
            operationName: "GetCustomers"
          })
        }
      }}>
        <Suspense fallback={<>Loading...</>} >
          <LazyTable />
        </Suspense>
      </DataProvider>
    </>
  )
}
