import { useEffect, useState, useRef } from 'react'
import { useStreamCustomersByFnSubscription } from '../generated/graphql';
import StreamTable from './StreamTable';


export const Search = () => {
  const [searchTerm, setSearchTerm] = useState<string>('');
  const { data, loading } = useStreamCustomersByFnSubscription({
    variables: { fn_ilike: searchTerm },
  })
  const poolData = useRef<any>([]);
  const handleChange = (e: any) => {
    setSearchTerm(`${e.target.value}%`);
  }

  useEffect(() => {
    if (!loading) {
      poolData.current = [...poolData, ...(data ? data?.customers_stream : [])]
    }

  }, [loading, data, poolData])



  return (
    <>
      <input
        onChange={handleChange}
      />
      {data &&
        <StreamTable data={poolData} />

      }
    </>
  )
}
