import { gql, useApolloClient } from '@apollo/client';
import { useEffect, useState, useCallback } from 'react'
import { GetCustomersLikeNameDocument, useStreamCustomersByFnSubscription } from '../generated/graphql';
import StreamTable from './StreamTable';

export type Rows = {
  __typename?: "customers";
  email_address?: string | null;
  first_name?: string | null;
  last_name?: string | null;
}[]

export const Search = () => {
  const [searchTerm, setSearchTerm] = useState<string>('');
  const { data } = useStreamCustomersByFnSubscription({
    variables: { fn_ilike: searchTerm },
  })
  const [poolData, setPoolData] = useState<Rows>([]);
  const handleChange = (e: any) => {
    console.log(e.target.value === '', searchTerm)
    setPoolData([])
    if (e.target.value === '') {
      return setSearchTerm('')
    }
    setSearchTerm(`${e.target.value}%`);
  }

  useEffect(() => {
    if (searchTerm === '') {
      return
    }
    setPoolData(p => [...p, ...(data ? data?.customers_stream : [])])
  }, [data, searchTerm])




  const client = useApolloClient()

  console.log(client)


  /**
   * gql`
    query GetCustomersLikeName($limit: Int, $first_name_term: String) {
  customers(limit: $limit, where: {first_name: {_like: $first_name_term}}) {
    email_address
    first_name
    last_name
  }
}
    `
   */

  const readStreamCache = gql`
    query readStreamCache($limit: Int, $first_name_term: String) {
    customers_stream(limit: $limit, where: {first_name: { _ilike: $first_name_term }}) {
    email_address
    first_name
    last_name
  }
}
  `

  const readStreamCacheFragment = client.read


  const customers_stream = client.readQuery({
      query: readStreamCache,
      variables: {
        limit: 1000,
        first_name_term: searchTerm
      }
    })

  console.log(customers_stream)

  return (
    <>
      <input
        placeholder='search by first name'
        onChange={handleChange}
      />
      {data &&
        <StreamTable data={poolData} />

      }
    </>
  )
}
