import React, { lazy, Suspense } from 'react'
import '../App.css';
import { DataContext } from '../Context/DataContext';
import { useGetData } from '../hooks/useGetData';
import { CallbackButton } from './CallbackButton';

async function fetchGraphQL(operationsDoc: any, operationName: any, variables: any) {
  const result = await fetch(
    "http://localhost:8080/v1/graphql",
    {
      method: "POST",
      body: JSON.stringify({
        query: operationsDoc,
        variables: variables,
        operationName: operationName
      })
    }
  );

  return await result.json();
}

const operationsDoc = `
  query GetCustomers {
    customers(limit: 10) {
      email_address
      first_name
      last_name
    }
  }
`;

function fetchGetCustomers() {
  return fetchGraphQL(
    operationsDoc,
    "GetCustomers",
    {}
  );
}

async function startFetchGetCustomers() {
  const { errors, data } = await fetchGetCustomers();

  if (errors) {
    console.error(errors);
  }
  // do something great with this precious data
  return data.data.customers;
}

const LazyTable  = lazy(() => import('./Table'))

export const Display = ({ url }: { url: string }) => {
  const onAdd = () => {
    const url = 'http://localhost:3000/addcustomer'
    const options = {
      method: "POST",
      headers: {
        'Content-type': 'application/json'
      },
      body: JSON.stringify({
        customerId: "this-is-a-customerId",
        email: "test@test.com",
        first_name: "Lebron",
        last_name: "James",
      })
    }

    fetch(url, options)
  }

  const onRemove = () => {
    const url = 'http://localhost:3000/removecustomer'
    const options = {
      method: "POST",
      headers: {
        'Content-type': 'application/json'
      },
      body: JSON.stringify({
        customerId: "this-is-a-customerId",
      })
    }

    fetch(url, options)
  }

  return (
    <DataContext.Provider value>
      <CallbackButton callback={onAdd} text="Add test Customer" />
      <CallbackButton callback={onRemove} text="remove test Customer" />
      <Suspense fallback={<>Loading...</>} >
        <LazyTable />
      </Suspense>
    </DataContext.Provider>
  )
}
