import { useState } from 'react'
import '../App.css';
import { GetCustomersLikeNameQuery, useCreateCustomerMutation, useDeleteCustomerByPkMutation, useGetCustomersLikeNameLazyQuery } from '../generated/graphql';
import { CallbackButton } from './CallbackButton';
import { Table } from './Table';

export type ContextType = {
  data: {
    data: {
      customers: { email_address: string, first_name: string, last_name: string }[]
    }
  }
}

export const Display = () => {
  const [getCustomers] = useGetCustomersLikeNameLazyQuery({
    fetchPolicy: 'network-only',
    variables: { limit: 19, first_name_term: "Le%" }
  });
  const [results, setResults] = useState<GetCustomersLikeNameQuery>(data)
  const [deleteCustomerByPkMutation] = useDeleteCustomerByPkMutation({
    fetchPolicy: 'network-only',
    onCompleted: () => getCustomers().then(res => res?.data && setResults(res.data))
  })
  const [createCustomerMutation] = useCreateCustomerMutation({
    fetchPolicy: "network-only",
    onCompleted: () => getCustomers().then(res => res?.data && setResults(res.data))
  })
  const onAdd = () => {
    createCustomerMutation({
      variables: {
        customer_id: "this-is-a-customerId",
        email_address: "test@test.com",
        last_name: "James",
        first_name: "LeBron"
      }
    })
  }

  const onRemove = () => {
    deleteCustomerByPkMutation({ variables: { customer_id: "this-is-a-customerId" } })
  }

  return (
    <>
      <CallbackButton callback={onAdd} text="Add"></CallbackButton>
      <CallbackButton callback={onRemove} text="remove"></CallbackButton>
      { results.customers.length !== 0 &&
        <Table data={results} />
      }
    </>
  )
}

export default Display;
